import React from 'react';
import './ProfileSection.css';

const ProfileSection = ({ playerInfo }) => {
  const getStatusText = (state) => {
    const states = {
      0: '–û—Ñ—Ñ–ª–∞–π–Ω',
      1: '–û–Ω–ª–∞–π–Ω',
      2: '–ó–∞–Ω—è—Ç',
      3: '–û—Ç–æ—à—ë–ª',
      4: '–°–ø–∏—Ç',
      5: '–ì–æ—Ç–æ–≤ –∫ –æ–±–º–µ–Ω—É',
      6: '–ì–æ—Ç–æ–≤ –∏–≥—Ä–∞—Ç—å'
    };
    return states[state] || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ';
  };

  const formatDate = (timestamp) => {
    if (!timestamp) return '–ù–µ —É–∫–∞–∑–∞–Ω–æ';
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString('ru-RU', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getTimeSince = (timestamp) => {
    if (!timestamp) return '';
    const now = new Date();
    const then = new Date(timestamp * 1000);
    const diffYears = now.getFullYear() - then.getFullYear();
    const diffMonths = now.getMonth() - then.getMonth();
    const totalMonths = diffYears * 12 + diffMonths;
    const years = Math.floor(totalMonths / 12);
    const months = totalMonths % 12;
    
    let result = '';
    if (years === 0) {
      if (months === 0) return '–ú–µ–Ω—å—à–µ –º–µ—Å—è—Ü–∞';
      if (months === 1) return '1 –º–µ—Å—è—Ü';
      if (months < 5) return `${months} –º–µ—Å—è—Ü–∞`;
      return `${months} –º–µ—Å—è—Ü–µ–≤`;
    }
    
    if (years === 1) {
      result = '1 –≥–æ–¥';
    } else if (years < 5) {
      result = `${years} –≥–æ–¥–∞`;
    } else {
      result = `${years} –ª–µ—Ç`;
    }
    
    if (months > 0) {
      if (months === 1) {
        result += ' 1 –º–µ—Å—è—Ü';
      } else if (months < 5) {
        result += ` ${months} –º–µ—Å—è—Ü–∞`;
      } else {
        result += ` ${months} –º–µ—Å—è—Ü–µ–≤`;
      }
    }
    
    return result;
  };

  const getAccountRank = (timestamp) => {
    if (!timestamp) return { title: '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ', icon: '‚ùì' };

    const now = new Date();
    const then = new Date(timestamp * 1000);
    const diffYears = now.getFullYear() - then.getFullYear();
    const diffMonths = now.getMonth() - then.getMonth();
    const totalMonths = diffYears * 12 + diffMonths;
    
    if (totalMonths < 1) return { title: '–ù–æ–≤–∏—á–æ–∫', icon: 'üå±' };
    if (totalMonths < 6) return { title: '–ù–∞—á–∏–Ω–∞—é—â–∏–π', icon: 'üéÆ' };
    if (totalMonths < 12) return { title: '–û–ø—ã—Ç–Ω—ã–π –∏–≥—Ä–æ–∫', icon: 'üéØ' };
    if (diffYears < 3) return { title: '–≠–Ω—Ç—É–∑–∏–∞—Å—Ç Steam', icon: '‚≠ê' };
    if (diffYears < 5) return { title: '–í–µ—Ç–µ—Ä–∞–Ω Steam', icon: 'üèÜ' };
    if (diffYears < 8) return { title: '–ú–∞—Å—Ç–µ—Ä Steam', icon: 'üëë' };
    if (diffYears < 10) return { title: '–õ–µ–≥–µ–Ω–¥–∞ Steam', icon: 'üåü' };
    return { title: '–î—Ä–µ–≤–Ω–∏–π —Å—Ç—Ä–∞–∂ Steam', icon: 'üè∞' };
  };

  if (!playerInfo) return null;

  const profileAge = getTimeSince(playerInfo.timecreated);
  const accountRank = getAccountRank(playerInfo.timecreated);

  return (
    <div className="profile-section">
      <div className="profile-header">
        <div className="profile-avatar-wrapper">
          <div className="profile-avatar-container">
            <img src={playerInfo.avatarfull} alt="Avatar" className="profile-avatar" />
            <div className={`status-indicator ${playerInfo.personastate === 1 ? 'online' : 'offline'}`} />
          </div>
          <div className="profile-quick-stats">
            <div className="quick-stat">
              <span className="stat-icon">üéÆ</span>
              <span className="stat-value">{playerInfo.gameextrainfo || '–ù–µ –≤ –∏–≥—Ä–µ'}</span>
            </div>
            <div className="quick-stat">
              <span className="stat-icon">üåç</span>
              <span className="stat-value">{playerInfo.loccountrycode || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}</span>
            </div>
          </div>
        </div>
        
        <div className="profile-info">
          <div className="profile-name-container">
            <h2 className="profile-name">{playerInfo.personaname}</h2>
            <div className="profile-status" data-status={playerInfo.personastate}>
              {getStatusText(playerInfo.personastate)}
            </div>
          </div>
          
          {playerInfo.realname && (
            <div className="profile-realname">
              <span className="realname-icon">üë§</span>
              {playerInfo.realname}
            </div>
          )}

          <div className="profile-badges">
            {profileAge && (
              <div className="profile-badge">
                <span className="badge-icon">{accountRank.icon}</span>
                <span className="badge-text">{accountRank.title}</span>
                <span className="badge-subtext">{profileAge}</span>
              </div>
            )}
            {playerInfo.communityvisibilitystate === 3 && (
              <div className="profile-badge">
